<?php   // 创建连接   $conn = new mysqli("127.0.0.1", "yangwenguang", "123456","charles");   // 检测连接   if ($conn->connect_error)   {die("连接失败: " . $conn->connect_error);}   // 使用 sql 创建数据表   $sql = "CREATE TABLE conan(   id INT(10) UNSIGNED AUTO_INCREMENT PRIMARY KEY,   title VARCHAR(30) NOT NULL,   tag1 VARCHAR(30) NOT NULL,   tag2 VARCHAR(30) NOT NULL,   tag3 VARCHAR(30) NOT NULL,   digest VARCHAR(255) NOT NULL,   ip VARCHAR(30) NOT NULL,   date VARCHAR(30) NOT NULL   )";   if ($conn->query($sql) === TRUE)   {echo "Table MyGuests created successfully";}   else {echo "创建数据表错误: " . $conn->error;}   $conn->close();?>